window.getClientId = function () {
  var gaMatch = document.cookie.match(/_ga=GA1.1.([0-9\.]+)/);
  return gaMatch ? gaMatch[1] : null;
};

(function () {
  var ua = navigator.userAgent || "";
  var isIOS = /iPad|iPhone|iPod/.test(ua);
  if (!isIOS) return;
  var ignore = window.flutterfire_ignore_scripts;
  if (!Array.isArray(ignore)) {
    ignore = [];
  }
  if (ignore.indexOf("messaging") === -1) {
    ignore.push("messaging");
  }
  window.flutterfire_ignore_scripts = ignore;
})();

(function () {
  var startupSplashHidden = false;

  function hideStartupSplash() {
    if (startupSplashHidden) {
      return;
    }
    startupSplashHidden = true;

    var splash = document.getElementById('app-startup-splash');
    if (!splash) {
      return;
    }

    splash.classList.add('is-hidden');
    window.setTimeout(function () {
      if (splash.parentNode) {
        splash.parentNode.removeChild(splash);
      }
    }, 260);
  }

  function watchFlutterSurfaceAsFallback() {
    if (!window.MutationObserver) {
      return;
    }

    var root = document.body || document.documentElement;
    if (!root) {
      window.setTimeout(watchFlutterSurfaceAsFallback, 0);
      return;
    }

    var observer = new MutationObserver(function () {
      if (document.querySelector('flt-glass-pane')) {
        observer.disconnect();
        hideStartupSplash();
      }
    });

    observer.observe(root, { childList: true, subtree: true });

    window.setTimeout(function () {
      observer.disconnect();
    }, 30000);
  }

  window.addEventListener('flutter-first-frame', hideStartupSplash, { once: true });

  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', watchFlutterSurfaceAsFallback, { once: true });
  } else {
    watchFlutterSurfaceAsFallback();
  }
})();

