(function () {
  var cfg = window.__ideallyaAndroidAppLinks;
  if (!cfg || !cfg.packageName || !cfg.playStoreUrl) return;
  var ua = navigator.userAgent || '';
  if (!/android/i.test(ua)) return;
  if (/\bwv\b/.test(ua) || /;\s*wv\)/.test(ua)) return;
  var shouldAuto = cfg.autoRedirect === true;
  if (!shouldAuto) {
    try {
      var params = new URLSearchParams(window.location.search || '');
      var key = (cfg.autoRedirectParam || 'openApp');
      var v = (params.get(key) || '').toLowerCase();
      shouldAuto = v === '1' || v === 'true' || v === 'yes';
    } catch (_) {}
  }
  if (!shouldAuto) return;
  try {
    var onceKey = '__ideallya_android_intent_tried';
    if (sessionStorage.getItem(onceKey) === '1') return;
    sessionStorage.setItem(onceKey, '1');
  } catch (_) {}
  var fallback = encodeURIComponent(cfg.playStoreUrl);
  var hostAndPath = window.location.host + window.location.pathname + window.location.search;
  var intent = 'intent://' + hostAndPath + '#Intent;scheme=https;package=' + cfg.packageName + ';S.browser_fallback_url=' + fallback + ';end';
  window.location.replace(intent);
})();

